sys_script_client.script:
function onLoad() {

    var dateToCheck = g_form.getValue('follow_up'); // Get the follow up date

    var ga = new GlideAjax('ActionDateUtils'); // Script Include name
    ga.addParam('sysparm_name', 'isDueDateBeforeToday'); // Function name in Script Include
    ga.addParam('sysparm_my_param', dateToCheck); // Custom parameter
    ga.getXML(myFunctionCallback); // Call Script Include and specify callback

    function myFunctionCallback(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer'); // For getXML

        if (answer == "true") {
            g_form.getControl('follow_up').style.backgroundColor = 'Red'; //Using getControl to set background
			g_form.setValue('action_required',true);
        } else {
            g_form.getControl('follow_up').style.backgroundColor = ''; // Reset to default
			g_form.setValue('action_required',false);
        }
    }
}
