sys_script_include.script:
var ActionDateUtils = Class.create();
ActionDateUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    // ##################Begin###############################################
    // Calculate if the date provided is before today date

    isDueDateBeforeToday: function() {
        // Get the current date and time
        var gdtToday = new GlideDateTime();

        // Convert the input due date string to a GlideDateTime object
        var myParam = this.getParameter('sysparm_my_param'); // Retrieve parameter from client
        var gdtDueDate = new GlideDateTime();
        gdtDueDate.setDisplayValue(myParam); // Assumes dueDateString is in user's display format

        // Compare the due date with today's date
        if (gdtDueDate.before(gdtToday)) {
            return true; // Due date is before today
        } else {
            return false; // Due date is today or in the future
        }
    },
    // #################END###################################################

    type: 'ActionDateUtils'
});
